import React from 'react';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from 'recharts';

const CustomTooltip = ({ active, payload, formatCurrency }) => {
  if (active && payload && payload.length) {
    const data = payload[0].payload;
    return (
      <div className="custom-tooltip">
        <p className="label">{data.name}</p>
        <p className="value">Value: {formatCurrency(data.value)}</p>
        <p className="count">Deals: {data.count}</p>
      </div>
    );
  }
  return null;
};

const FunnelChart = ({ data, formatCurrency }) => {
  // Sort data to ensure it's in the correct funnel order
  const sortedData = [...data].sort((a, b) => b.value - a.value);

  return (
    <ResponsiveContainer width="100%" height={350}>
      <BarChart
        data={sortedData}
        margin={{ top: 20, right: 30, left: 20, bottom: 40 }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        {/* X axis: stages, rotated for readability */}
        <XAxis 
          type="category" 
          dataKey="name" 
          angle={-45} 
          textAnchor="end" 
          interval={0} 
          height={70} 
        />
        <YAxis type="number" />
        <Tooltip content={<CustomTooltip formatCurrency={formatCurrency} />} />
        <Legend />
        <Bar 
          dataKey="value" 
          name="Deal Value" 
          fill="#1a56db" 
          radius={[4, 4, 0, 0]}
        />
        <Bar 
          dataKey="count" 
          name="Deal Count" 
          fill="#7e3af2" 
          radius={[4, 4, 0, 0]}
        />
      </BarChart>
    </ResponsiveContainer>
  );
};

export default FunnelChart;
